import React from 'react'
import Candidate from '../Items/Candidate'
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';


function Return()
{
    window.location.reload(true)
}
const ApplicantsList = (props) => {
console.log(props)
  return(
 <> 
   {/* <label htmlFor="search">Search by Title </label>
   <input type="text" value ={props.inputValue}  onChange={props.jobFilterOnChange}/> */}
     <div>
    <Button color="secondary" onClick={Return}>Return To All Jobs</Button>
    <h3>{props.job.title} Applications</h3>
      <TableContainer component={Paper}>
      <Table  aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Name</TableCell>
            <TableCell align="right">Skills</TableCell>
            <TableCell align="right">Date of Application</TableCell>   
            <TableCell align="right">Statement of Purpose</TableCell>   
            {/* uncomment for date field and corresponding table row in applicant item  */}

            <TableCell align="right">Rating</TableCell>
            <TableCell align="right">Status</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
        {
           props.users.map(user => {
           return <Candidate user={user} key={user._id} job={props.job} onClickAccept={props.onClickAccept} doa={props.doa} toggleDoUpdate={props.toggleDoUpdate}sop={props.sop}/>
           })
        }
        </TableBody>
      </Table>
    </TableContainer>
        {/* <SimpleModal user={props.user}/> */}
     </div>
  </>
    )

   }


export default ApplicantsList
