import React, {Component} from 'react';
import ReactDOM from "react-dom"
import Card from 'react-bootstrap/Card';
import axios from 'axios';
import { validateFields } from '../../Common/Validation';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import alertify from 'alertifyjs';
import 'alertifyjs/build/css/alertify.css';

function printSkills(props){
        return (
            <div>
                {props}
            </div>
        )
    }

class Employee extends Component {
    //console.log(props);

    constructor(props) {
        super(props);
        this.state = {
           showComponent: false,
           rating:"1"
        };
        // console.log("in JobElement");
        this.rate_me='';
        this.onClickRate=this.onClickRate.bind(this)
    }
    handleRatingChange =(event) =>{
        this.setState({rating:event.target.value})
    }
    onClickRate(){
        // console.log("hi")
        // console.log(this.state.rating)
        const Sending={
            email:this.props.email,
            rating: this.state.rating,
            id:this.props.user.id,
        }
        // console.log(Sending)
        axios.post("http://localhost:4000/user/rateuser",Sending)
        .then(res => {
            // console.log(res);
            // console.log(res.data);

        })
        .catch(function(error) {
            console.log(error);
        })
        window.location.reload()
    }

        render(){
            console.log(this.state)
            this.rate_me=<div>
                            <select name="rating" onChange={this.handleRatingChange}>
                            <option value="1">1</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                            </select> 
                            <Button color='primary' size='small' onClick={this.onClickRate}>Submit Rating</Button>
                            </div>
                this.props.user.has_rated.map(item =>{
                    if(item.email===this.props.email)
                    {
                        this.rate_me=item.rating;
                    }
                })

            console.log(this.props)
            return(
                <TableRow key={this.props.user._id}>
                    <TableCell>{this.props.user.name}</TableCell>
                    <TableCell align="right">{this.props.user.title}</TableCell>
                    <TableCell align="right">{this.props.user.doj}</TableCell>
                    <TableCell align="right">{this.props.user.type}</TableCell>
                    <TableCell align="right">{this.props.user.rating}</TableCell>
                    <TableCell align="right">{this.rate_me}</TableCell>
                    {/* {this.state.showComponent?<SimplePortal /> :
                                            <div> <TableCell align="right">{this.status}</TableCell>
                                            <br/></div>
                                            } */}
                </TableRow>
            )
        }
    }

export default Employee;
