import React, {Component} from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import JobStatus from '../Items/JobStatus'

function Return()
{
    window.location.reload(true)
}
class AppStatus extends Component {
    constructor(props) {
        super(props);
        this.state = {
           sortValue: 'Name',
           sortOrder: 'Ascending',
           isLoading: true,
        };
        this.jobs=[]
        this.email=""
        this.user=""
    }
    componentDidMount() {
        // console.log(this.state.check_box)
        // console.log(this.state.check_box["a"])
        axios.get('http://localhost:4000/job')
             .then(response => {
                //  this.setState({jobs: response.data});
                 this.jobs=response.data;
                //  console.log(this.jobs)
                 axios.get("http://localhost:4000/user/session")
                .then(res => {
                    // console.log(res);
                    // console.log(res.data[0].email);
                    this.email = res.data[0].email;
                    //console.log(this.email);
                    // console.log(this.email);
                    //console.log(this.jobs)
                    const JobElements = this.jobs.map(item =>{ 
                                                    //console.log(item)
                                                    if(item.rejected.includes(this.email) || item.accepted.includes(this.email)
                                                    || item.applicants.includes(this.email) || item.shortlisted.includes(this.email)){
                                                        return item;
                                                    }
                                                    else{
                                                        return null;
                                                    }
                                                });
                    var _ = require('underscore');
                    var thinboi = _.compact(JobElements);                            
                    this.jobs=thinboi;
                    this.setState({isLoading: false});
                    console.log("done");

             })
             .catch(function(error) {
                 console.log(error);
             })
                //  console.log(response);
                // console.log(this.state.jobs);
             })
             .catch(function(error) {
                 console.log(error);
             })


    }
render(){

    if(this.state.isLoading){
        return(
            <div>
                Loading...
            </div>
        )
    }
    else{
        //console.log(this.jobs)
    return(
    <> 
    {/* <label htmlFor="search">Search by Title </label>
    <input type="text" value ={props.inputValue}  onChange={props.jobFilterOnChange}/> */}
        <div>
        {/* <Button color="secondary" onClick={Return}>Return To All Jobs</Button> */}

        <TableContainer component={Paper}>
        <Table  aria-label="simple table">
            <TableHead>
            <TableRow>
                <TableCell>Title</TableCell>
                <TableCell align="right">Salary</TableCell>
                <TableCell align="right">Date of Joining</TableCell>   
                {/* uncomment for date field and corresponding table row in applicant item  */}
                <TableCell align="right">Name of Recruiter</TableCell>
                <TableCell align="right">Status </TableCell>
                <TableCell align="right">Rate This Job </TableCell>
            </TableRow>
            </TableHead>
            <TableBody>
            {
            this.jobs.map(job => {
            return <JobStatus email={this.email} job={job}/>
            })
            }
            </TableBody>
        </Table>
        </TableContainer>
            {/* <SimpleModal user={props.user}/> */}
        </div>
    </>
        )
        }
    }
}


export default AppStatus
